import React from "react";
import { connect } from "react-redux";
import WithSpinner from "../../components/with-spinner/with-spinner.component";
import { Outlet, Routes, Route } from "react-router-dom";

import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import { collection, firestore, onSnapshot, convertCollectionsSnapShotToMap } from "../../firebase/firebase.utils";
import { updateCollections } from "../../redux/shop/shop.actions";

import './shop.styles.scss';
import CollectionPage from "../collection/collection.component";

class ShopPage extends React.Component {
  state = {
    loading: true,
  }
  unsubscribeFromSnapShot = null;

  componentDidMount() {
    const { updateCollections } = this.props;
    const collectionRef = collection(firestore, 'collections');
    this.unsubscribeFromSnapShot = onSnapshot(collectionRef, (snapShot) => {
      const collectionsMap = convertCollectionsSnapShotToMap(snapShot);
      updateCollections(collectionsMap);
      this.setState({ loading: false })
    })
  }

  render() {
    return (
      <div className="shop-page">
        <Routes>
        {/* <CollectionsOverview /> */}
        <Route path="/" element={<CollectionsOverview />} />
        {/* <Route path="/shop/:" element={<CollectionsOverview />} /> */}
        <Route index path=":collectionId" element={<CollectionPage />} />

        </Routes>
        {/* <Outlet /> */}
      </div>
    )
  }

}

const mapDispatchToProps = dispatch => ({
  updateCollections: collectionsMap => dispatch(updateCollections((collectionsMap)))
});

export default connect(null, mapDispatchToProps)(ShopPage);
